  },
 "add": [
   {
      "rowNr": 3,
      "values": ["Hodnota pro první sloupec nového řádku s indexem 3", "Hodnota pro druhý sloupec nového řádku s indexem 3"]
   },
   {
      "values": ["Hodnota pro první sloupec nového řádku na konci tabulky", "Hodnota pro druhý sloupec nového řádku na konci tabulky"]
   }
 ]
